function newComment(like=0, user="You", comments=null, fakeComment=false){
    if (comments == null){
        input = document.querySelector("#input").value;
    }
    else{
        input = comments;
    }
    const container = document.querySelector("#comments");

    const commentCard = document.createElement("div");
    commentCard.setAttribute("class", "card shadow-sm mb-4 border");

    const cardBody = document.createElement("div");
    cardBody.setAttribute("class", "card-body");

    const comment = document.createElement("p");
    comment.className = "lead";
    comment.innerHTML = input;


    const avatar = document.createElement("img");
    if (user == "You"){
        avatar.setAttribute("src", "https://mdbcdn.b-cdn.net/img/Photos/Avatars/img%20(20).webp");
    }
    else{
        let randomImg = Math.floor(Math.random() * (names.length-1))+1;
        avatar.setAttribute("src", "https://mdbcdn.b-cdn.net/img/Photos/Avatars/img%20("+randomImg+").webp");
    }
    
    avatar.setAttribute("alt", "avatar");
    avatar.setAttribute("width", "25");
    avatar.setAttribute("height", "25");

    const avatarContainer = document.createElement("div");
    avatarContainer.setAttribute("class", "d-flex flex-row align-items-center");

    // TODO: Implement random user later
    const avatarText = document.createElement("p");
    avatarText.setAttribute("class", "mb-0 ms-2");
    avatarText.innerHTML = user;

    avatarContainer.appendChild(avatar);
    avatarContainer.appendChild(avatarText);

    const upvote = document.createElement("div");
    upvote.setAttribute("class", "d-flex flex-row align-items-center");

    const thumbsUp = document.createElement("i");
    thumbsUp.setAttribute("class", "fa fa-thumbs-o-up text-body");
    thumbsUp.style = "margin-right: 5px";

    const upvoteCount = document.createElement("p");
    upvoteCount.setAttribute("class", "small text-muted mb-0");
    upvoteCount.innerHTML = like;

    upvote.appendChild(thumbsUp);
    upvote.appendChild(upvoteCount);


    const time = document.createElement("p");
    time.className = "text-muted small mb-0";
    time.innerHTML = new Date().toString();
    cardBody.appendChild(time);


    cardBody.appendChild(comment);

    const infoContainer = document.createElement("div");
    infoContainer.setAttribute("class", "d-flex justify-content-between");
    infoContainer.appendChild(avatarContainer);
    infoContainer.appendChild(upvote);
    
    cardBody.appendChild(infoContainer);
    commentCard.appendChild(cardBody);
    container.appendChild(commentCard);

    document.querySelector("#input").value = "";
}

function randomComment(numberOfComment=3){
    let duplicateNumUser = [];
    let duplicateNumComment = [];
    let randomUser = "";
    let randomComment = "";

    for (let i=0; i<numberOfComment; i++){
        let randomLike = Math.floor(Math.random() * 100);
        while(true){
            randomUser = names[Math.floor(Math.random() * names.length)];
            if (!duplicateNumUser.includes(randomUser)){
                duplicateNumUser.push(randomUser);
                break;
            }
        }
        while(true){
            randomComment = comments[Math.floor(Math.random() * comments.length)];
            if (!duplicateNumComment.includes(randomComment)){
                duplicateNumComment.push(randomComment);
                break;
            }
        }
        
        newComment(randomLike, randomUser, randomComment, true);
    }
}

function removeComment(){
    let comment = document.querySelector("#comments");
    comment.innerHTML = "";
    console.log(comment);
}

